package com.medisys.model;


public class Patient {
    private int id;
    private String name;
    private String phone;
    private String dob;
    private String guardianRela;
    private String guardianPhone;
    private String guardianName;

    // Constructor for creating new patients (ID will be generated by DB)
    public Patient(String name, String phone, String dob) {
        this.name = name;
        this.phone = phone;
        this.dob = dob;
    }

    // Constructor for retrieving patients from DB (includes ID)
    public Patient(int id, String name, String phone, String dob) {
        this.id = id;
        this.name = name;
        this.phone = phone;
        this.dob = dob;
    }

    public Patient(int id, String name, String phone, String dob, String guardRela, String guardPhone, String guardName) {
        this.id = id;
        this.name = name;
        this.phone = phone;
        this.dob = dob;
        this.guardianName = guardName;
        this.guardianPhone = guardPhone;
        this.guardianRela = guardRela;
    }

    // Getters
    public int getId() {
        return id;
    }

    public String getName() {
        return name;
    }

    public String getPhone() {
        return phone;
    }

    public String getGuardianName() {
        return guardianName;
    }

    public String getGuardPhone() {
        return guardianPhone;
    }

    public String getGuardianRela() {
        return guardianRela;
    }

    public String getDOB() {
        return dob;
    }

    // Setters (if you need to modify properties after creation, e.g., phone number)
    public void setName(String name) {
        this.name = name;
    }

    public void setPhone(String phone) {
        this.phone = phone;
    }

    public void setID(Integer ID) {
        this.id = ID;
    }

    public void setDOB(String dob) {
        this.dob = dob;
    }

    public void setGuard(String guardRela, String guardName, String guardPhone) {
        this.guardianName = guardName;
        this.guardianPhone = guardPhone;
        this.guardianRela = guardRela;
    }
    // For debugging/printing
    @Override
    public String toString() {
        return "Patient{" +
               "id=" + id +
               ", name='" + name + '\'' +
               ", phone='" + phone + '\'' +
               '}';
    }
}